import React, { useState, useEffect } from 'react';
import { Settings, Save, Clock, DollarSign } from 'lucide-react';
import { getHotelSettings, updateHotelSettings } from '../services/api';

const HotelSettingsManagement = () => {
  const [settings, setSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    try {
      const data = await getHotelSettings();
      setSettings(data);
    } catch (error) {
      console.error('Error al cargar configuraciones:', error);
      alert('Error al cargar configuraciones');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);

    try {
      await updateHotelSettings(settings);
      alert('Configuraciones actualizadas exitosamente');
    } catch (error) {
      alert('Error al actualizar configuraciones');
    } finally {
      setSaving(false);
    }
  };

  const handleChange = (field, value) => {
    setSettings({ ...settings, [field]: value });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-3">
        <Settings className="w-8 h-8 text-gray-700" />
        <div>
          <h2 className="text-2xl font-bold text-gray-800">Configuraciones del Hotel</h2>
          <p className="text-sm text-gray-600">Gestiona horarios y cargos automáticos</p>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Check-out Settings */}
        <div className="card">
          <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
            <Clock className="w-5 h-5" />
            Configuración de Check-out
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="label">Hora Estándar de Check-out</label>
              <input
                type="time"
                className="input"
                value={settings?.standard_checkout_time || '12:00:00'}
                onChange={(e) => handleChange('standard_checkout_time', e.target.value)}
              />
              <p className="text-xs text-gray-500 mt-1">Hora límite para salida sin cargo extra</p>
            </div>

            <div>
              <label className="label">Margen de Gracia (minutos)</label>
              <input
                type="number"
                className="input"
                min="0"
                max="120"
                value={settings?.grace_period_minutes || 30}
                onChange={(e) => handleChange('grace_period_minutes', parseInt(e.target.value))}
              />
              <p className="text-xs text-gray-500 mt-1">Tiempo extra sin cargo</p>
            </div>

            <div>
              <label className="label">Cargo por Hora Extra (%)</label>
              <input
                type="number"
                className="input"
                min="0"
                max="100"
                step="0.01"
                value={settings?.late_checkout_charge_per_hour || 15}
                onChange={(e) => handleChange('late_checkout_charge_per_hour', parseFloat(e.target.value))}
              />
              <p className="text-xs text-gray-500 mt-1">% del precio base por hora de retraso</p>
            </div>

            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                id="late_checkout_enabled"
                checked={settings?.late_checkout_enabled || false}
                onChange={(e) => handleChange('late_checkout_enabled', e.target.checked)}
                className="w-5 h-5"
              />
              <label htmlFor="late_checkout_enabled" className="text-sm font-medium">
                Activar cobro por salida tardía
              </label>
            </div>
          </div>
        </div>

        {/* Check-in Settings */}
        <div className="card">
          <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
            <Clock className="w-5 h-5" />
            Configuración de Check-in
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="label">Hora Estándar de Check-in</label>
              <input
                type="time"
                className="input"
                value={settings?.standard_checkin_time || '15:00:00'}
                onChange={(e) => handleChange('standard_checkin_time', e.target.value)}
              />
              <p className="text-xs text-gray-500 mt-1">Hora de entrada permitida</p>
            </div>

            <div>
              <label className="label">Cargo Early Check-in (%)</label>
              <input
                type="number"
                className="input"
                min="0"
                max="100"
                step="0.01"
                value={settings?.early_checkin_charge_percent || 50}
                onChange={(e) => handleChange('early_checkin_charge_percent', parseFloat(e.target.value))}
              />
              <p className="text-xs text-gray-500 mt-1">% adicional por entrada anticipada</p>
            </div>

            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                id="early_checkin_enabled"
                checked={settings?.early_checkin_enabled || false}
                onChange={(e) => handleChange('early_checkin_enabled', e.target.checked)}
                className="w-5 h-5"
              />
              <label htmlFor="early_checkin_enabled" className="text-sm font-medium">
                Activar cobro por entrada anticipada
              </label>
            </div>
          </div>
        </div>

        {/* Weekend Rates */}
        <div className="card">
          <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
            <DollarSign className="w-5 h-5" />
            Tarifa Fin de Semana
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="label">Incremento Fin de Semana (%)</label>
              <input
                type="number"
                className="input"
                min="0"
                max="100"
                step="0.01"
                value={settings?.weekend_rate_percent || 10}
                onChange={(e) => handleChange('weekend_rate_percent', parseFloat(e.target.value))}
              />
              <p className="text-xs text-gray-500 mt-1">Incremento para viernes, sábado y domingo</p>
            </div>

            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                id="weekend_rate_enabled"
                checked={settings?.weekend_rate_enabled || false}
                onChange={(e) => handleChange('weekend_rate_enabled', e.target.checked)}
                className="w-5 h-5"
              />
              <label htmlFor="weekend_rate_enabled" className="text-sm font-medium">
                Activar tarifa de fin de semana
              </label>
            </div>
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-end">
          <button
            type="submit"
            disabled={saving}
            className="btn-primary flex items-center gap-2"
          >
            <Save className="w-5 h-5" />
            {saving ? 'Guardando...' : 'Guardar Configuraciones'}
          </button>
        </div>
      </form>
