import React, { useState, useEffect } from 'react';
import { Users, Plus, DollarSign, LogOut as CheckOutIcon } from 'lucide-react';
import GuestRegistrationForm from './GuestRegistrationForm';
import CheckoutModal from './CheckoutModal';
import AddChargesModal from './AddChargesModal';
import { searchBookings, getLateCheckoutPreview } from '../../services/api';

const ActiveGuestsList = () => {
  const [guests, setGuests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showRegistration, setShowRegistration] = useState(false);
  const [selectedGuest, setSelectedGuest] = useState(null);
  const [showCheckout, setShowCheckout] = useState(false);
  const [showAddCharges, setShowAddCharges] = useState(false);
  
  // ‚úÖ NUEVO ESTADO: Para almacenar los cargos de late checkout por ID de hu√©sped/reserva.
  const [lateCheckoutInfo, setLateCheckoutInfo] = useState({}); 

// ... El resto del componente contin√∫a aqui
// [BLOQUE 1] - REEMPLAZO COMPLETO DE L√ìGICA Y MANEJADORES

  // Bloque de Efecto para cargar datos
  useEffect(() => {
    loadGuests();
    // Refresco peri√≥dico para actualizar cargos de Late Checkout
    const interval = setInterval(loadGuests, 300000); // 5 minutos
    return () => clearInterval(interval);
  }, []);

  // Bloque de L√≥gica de Carga de Hu√©spedes
  const loadGuests = async () => {
    setLoading(true);
    try {
      const response = await searchBookings({ status: 'checked_in' });
      const fetchedGuests = response.data;
      setGuests(fetchedGuests);

      const previewPromises = fetchedGuests.map(guest => 
        getLateCheckoutPreview(guest.id)
          .then(data => ({ id: guest.id, ...data }))
          .catch(() => ({ id: guest.id, late_checkout_charge: 0, new_balance: parseFloat(guest.balance || 0) }))
      );

      const results = await Promise.all(previewPromises);
      
      const newLateInfo = results.reduce((acc, curr) => {
        acc[curr.id] = curr;
        return acc;
      }, {});
      setLateCheckoutInfo(newLateInfo);

    } catch (error) {
      console.error("Error cargando hu√©spedes y previews:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCheckout = (guest) => {
    setSelectedGuest(guest);
    setShowCheckout(true);
  };

  const handleAddCharges = (guest) => {
    setSelectedGuest(guest);
    setShowAddCharges(true);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold flex items-center gap-2">
          <Users className="w-6 h-6" />
          Hu√©spedes Activos ({guests.length})
        </h2>
        <button
          onClick={() => setShowRegistration(true)}
          className="btn-success flex items-center gap-2"
        >
          <Plus className="w-5 h-5" />
          Registrar Hu√©sped
        </button>
      </div>

      {guests.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {guests.map((guest) => {
            // Calcular valores
            const total = parseFloat(guest.total_amount || 0);
            const lateCharge = parseFloat(guest.late_checkout_charge || 0);
            const paid = parseFloat(guest.amount_paid || 0);
            const balance = parseFloat(guest.current_balance || guest.balance || 0);
            const isLate = guest.is_late && lateCharge > 0;

            return (
              <div key={guest.id} className="card hover:shadow-lg transition-shadow">
                {/* Alerta de Late Checkout */}
                {isLate && (
                  <div className="bg-red-50 border-l-4 border-red-500 p-2 mb-3 rounded">
                    <p className="text-xs font-bold text-red-800">
                      üö® SALIDA TARD√çA: {guest.late_checkout_hours}h
                    </p>
                  </div>
                )}

                {/* Cabecera */}
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className="font-bold text-lg">{guest.full_name}</h3>
                    <p className="text-sm text-gray-600">CI: {guest.document_number}</p>
                  </div>
                  <span className={`badge ${isLate ? 'badge-danger' : 'badge-success'}`}>
                    Hab. {guest.room_number}
                  </span>
                </div>

                {/* Informaci√≥n */}
                <div className="space-y-2 text-sm mb-4">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Check-in:</span>
                    <span className="font-semibold">
                      {new Date(guest.check_in).toLocaleString('es-BO', {
                        day: '2-digit',
                        month: '2-digit',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Salida esperada:</span>
                    <span className={`font-semibold ${isLate ? 'text-red-600' : ''}`}>
                      {new Date(guest.expected_checkout).toLocaleString('es-BO', {
                        day: '2-digit',
                        month: '2-digit',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                  </div>
                </div>

                {/* Montos - CORREGIDO CON LATE CHECKOUT */}
                <div className="border-t pt-3 space-y-2 mb-4">
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Total habitaci√≥n:</span>
                    <span className="font-semibold">Bs. {total.toFixed(2)}</span>
                  </div>
                  
                  {/* ‚úÖ MOSTRAR LATE CHECKOUT SI EXISTE */}
                  {lateCharge > 0 && (
                    <div className="flex justify-between text-sm bg-orange-50 p-2 rounded">
                      <span className="text-orange-600 font-medium">
                        ‚è∞ Late Checkout ({guest.late_checkout_hours}h):
                      </span>
                      <span className="text-orange-600 font-semibold">
                        + Bs. {lateCharge.toFixed(2)}
                      </span>
                    </div>
                  )}
                  
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Pagado:</span>
                    <span className="text-green-600 font-semibold">
                      Bs. {paid.toFixed(2)}
                    </span>
                  </div>
                  
                  {/* ‚úÖ SALDO CORRECTO: Total + Late Checkout - Pagado */}
                  <div className="flex justify-between text-sm border-t pt-2">
                    <span className="text-gray-800 font-bold">SALDO:</span>
                    <span className={`font-bold text-lg ${
                      balance > 0.01 ? 'text-red-600' : 'text-green-600'
                    }`}>
                      Bs. {balance.toFixed(2)}
                    </span>
                  </div>

                  {/* Explicaci√≥n del c√°lculo si hay late checkout */}
                  {lateCharge > 0 && (
                    <div className="bg-blue-50 p-2 rounded text-xs text-gray-700">
                      <p className="font-medium">
                        üí° {total.toFixed(2)} + {lateCharge.toFixed(2)} - {paid.toFixed(2)} = {balance.toFixed(2)}
                      </p>
                    </div>
                  )}
                </div>

                {/* Botones */}
                <div className="grid grid-cols-2 gap-2">
                  <button
                    onClick={() => handleAddCharges(guest)}
                    className="btn-secondary text-sm flex items-center justify-center gap-1"
                  >
                    <DollarSign className="w-4 h-4" />
                    Cargos
                  </button>
                  <button
                    onClick={() => handleCheckout(guest)}
                    className={`text-sm flex items-center justify-center gap-1 ${
                      balance > 0.01 ? 'btn-warning' : 'btn-primary'
                    }`}
                  >
                    <CheckOutIcon className="w-4 h-4" />
                    {balance > 0.01 ? 'Cobrar' : 'Salida'}
                  </button>
                </div>

                {/* Advertencia si hay deuda */}
                {balance > 0.01 && (
                  <p className="text-xs text-center text-red-600 mt-2 font-medium">
                    ‚ö†Ô∏è Debe cobrar Bs. {balance.toFixed(2)}
                  </p>
                )}
              </div>
            );
          })}
        </div>
      ) : (
        <div className="card text-center py-12">
          <Users className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 text-lg mb-4">No hay hu√©spedes registrados</p>
          <button
            onClick={() => setShowRegistration(true)}
            className="btn-success mx-auto"
          >
            Registrar Primer Hu√©sped
          </button>
        </div>
      )}

      {/* Modales */}
      {showRegistration && (
        <GuestRegistrationForm
          onClose={() => setShowRegistration(false)}
          onCreated={() => {
            setShowRegistration(false);
            loadGuests();
          }}
        />
      )}

      {showCheckout && selectedGuest && (
        <CheckoutModal
          booking={selectedGuest}
          onClose={() => {
            setShowCheckout(false);
            setSelectedGuest(null);
          }}
          onSuccess={() => {
            setShowCheckout(false);
            setSelectedGuest(null);
            loadGuests();
          }}
        />
      )}

      {showAddCharges && selectedGuest && (
        <AddChargesModal
          guest={selectedGuest}
          onClose={() => {
            setShowAddCharges(false);
            setSelectedGuest(null);
          }}
          onAdded={() => {
            setShowAddCharges(false);
            setSelectedGuest(null);
            loadGuests();
          }}
        />
      )}
    </div>
  );
};

export default ActiveGuestsList;
