import React, { useState, useEffect } from 'react';
import { X, UserPlus } from 'lucide-react';
import { getAvailableRooms, createGuestBooking } from '../../services/api';

const GuestRegistrationForm = ({ onClose, onCreated }) => {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    // Datos del huésped
    full_name: '',
    document_number: '',
    age: '',
    nationality: 'Bolivia',
    origin: '',
    phone: '',
    
    // Datos de la reserva
    room_id: '',
    check_in: new Date().toISOString().slice(0, 16),
    expected_checkout: '',
    base_price: '',
    additional_income: '0',
  });

  useEffect(() => {
    loadRooms();
  }, []);

  const loadRooms = async () => {
    try {
      const data = await getAvailableRooms();
      setRooms(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleRoomSelect = (room) => {
    setFormData({
      ...formData,
      room_id: room.id,
      base_price: room.base_price
    });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);

  try {
    const bookingData = {
  full_name: formData.full_name,
  document_number: formData.document_number,
  phone: formData.phone,
  age: parseInt(formData.age),
  nationality: formData.nationality,
  origin: formData.origin,
  room_id: formData.room_id,
  check_in: formData.check_in,
  expected_checkout: formData.expected_checkout,
  stay_type: formData.stay_type || 'daily',  // ⬅️ NUEVO
  number_of_hours: formData.number_of_hours || null,  // ⬅️ NUEVO
  discount: parseFloat(formData.discount) || 0,  // ⬅️ NUEVO
  base_price: calculatePrice().total,  // ⬅️ MODIFICADO (usar total calculado)
  additional_income: parseFloat(formData.additional_income) || 0
};

    await createGuestBooking(bookingData);

    alert('Huésped registrado exitosamente');
    onCreated();
  } catch (error) {
    alert(error.response?.data?.error || 'Error al registrar huésped');
  } finally {
    setLoading(false);
  }
  };
  const calculatePrice = () => {
  if (!selectedRoom) return { basePrice: 0, total: 0 };
  
  let basePrice = parseFloat(selectedRoom.base_price);
  
  if (formData.stay_type === 'hourly') {
    // Precio por hora (puedes ajustar la fórmula)
    const hourlyRate = basePrice / 12; // Asumiendo que el precio diario es para 12 horas
    basePrice = hourlyRate * (formData.number_of_hours || 1);
  }
  
  const discount = parseFloat(formData.discount) || 0;
  const total = Math.max(0, basePrice - discount);
  
  return { basePrice, total };
};

  const selectedRoom = rooms.find(r => r.id === formData.room_id);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-y-auto">
      <div className="bg-white rounded-lg p-6 max-w-4xl w-full mx-4 my-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold flex items-center gap-2">
            <UserPlus className="w-6 h-6" />
            Registrar Nuevo Huésped
          </h2>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* DATOS DEL HUÉSPED */}
          <div className="card bg-blue-50">
            <h3 className="font-bold text-lg mb-4">Datos del Huésped</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="label">Nombre Completo *</label>
                <input
                  type="text"
                  className="input"
                  value={formData.full_name}
                  onChange={(e) => setFormData({...formData, full_name: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Número de Documento *</label>
                <input
                  type="text"
                  className="input"
                  value={formData.document_number}
                  onChange={(e) => setFormData({...formData, document_number: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Edad *</label>
                <input
                  type="number"
                  className="input"
                  min="1"
                  max="120"
                  value={formData.age}
                  onChange={(e) => setFormData({...formData, age: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Nacionalidad *</label>
                <input
                  type="text"
                  className="input"
                  value={formData.nationality}
                  onChange={(e) => setFormData({...formData, nationality: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Procedencia (Ciudad/País) *</label>
                <input
                  type="text"
                  className="input"
                  placeholder="Ej: La Paz, Bolivia"
                  value={formData.origin}
                  onChange={(e) => setFormData({...formData, origin: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Número de Teléfono *</label>
                <input
                  type="tel"
                  className="input"
                  placeholder="+59171234567"
                  value={formData.phone}
                  onChange={(e) => setFormData({...formData, phone: e.target.value})}
                  required
                />
              </div>
            </div>
          </div>

          {/* DATOS DE LA ESTADÍA */}
          <div className="card bg-green-50">
            <h3 className="font-bold text-lg mb-4">Datos de la Estadía</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="label">Hora de Ingreso *</label>
                <input
                  type="datetime-local"
                  className="input"
                  value={formData.check_in}
                  onChange={(e) => setFormData({...formData, check_in: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Hora de Salida Esperada *</label>
                <input
                  type="datetime-local"
                  className="input"
                  value={formData.expected_checkout}
                  onChange={(e) => setFormData({...formData, expected_checkout: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Ingresos Adicionales (Bs.)</label>
                <input
                  type="number"
                  step="0.01"
                  className="input"
                  value={formData.additional_income}
                  onChange={(e) => setFormData({...formData, additional_income: e.target.value})}
                  placeholder="0.00"
                />
              </div>
            </div>
          </div>

          {/* SELECCIÓN DE HABITACIÓN */}
          <div className="card bg-purple-50">
            <h3 className="font-bold text-lg mb-4">Seleccionar Habitación *</h3>
            
            {selectedRoom && (
              <div className="mb-4 p-3 bg-white rounded-lg border-2 border-purple-500">
                <p className="font-bold text-lg">Habitación {selectedRoom.room_number}</p>
                <p className="text-sm text-gray-600 capitalize">{selectedRoom.room_type}</p>
                <p className="text-xl font-bold text-green-600 mt-1">
                  Bs. {parseFloat(selectedRoom.base_price).toFixed(2)}
                </p>
              </div>
            )}
            {/* NUEVA SECCIÓN: Tipo de estadía */}
<div className="space-y-2">
  <label className="block text-sm font-medium text-gray-700">
    Tipo de Estadía *
  </label>
  <select
    value={formData.stay_type || 'daily'}
    onChange={(e) => {
      setFormData({ 
        ...formData, 
        stay_type: e.target.value,
        number_of_hours: e.target.value === 'hourly' ? 1 : null 
      });
    }}
    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
  >
    <option value="daily">Check-in / Check-out (Diario)</option>
    <option value="hourly">Por Horas</option>
  </select>
</div>

{/* NUEVO: Si es por horas, mostrar selector */}
{formData.stay_type === 'hourly' && (
  <div className="space-y-2">
    <label className="block text-sm font-medium text-gray-700">
      Cantidad de Horas *
    </label>
    <input
      type="number"
      min="1"
      max="24"
      value={formData.number_of_hours || 1}
      onChange={(e) => setFormData({ ...formData, number_of_hours: parseInt(e.target.value) })}
      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
    />
  </div>
)}

{/* NUEVA SECCIÓN: Check-in y Check-out */}
<div className="grid grid-cols-2 gap-4">
  <div className="space-y-2">
    <label className="block text-sm font-medium text-gray-700">
      Check-in
    </label>
    <input
      type="datetime-local"
      value={formData.check_in || new Date().toISOString().slice(0, 16)}
      onChange={(e) => {
        setFormData({ ...formData, check_in: e.target.value });
        // Auto-calcular checkout
        if (formData.stay_type === 'hourly' && formData.number_of_hours) {
          const checkInDate = new Date(e.target.value);
          const checkOutDate = new Date(checkInDate.getTime() + (formData.number_of_hours * 60 * 60 * 1000));
          setFormData(prev => ({ 
            ...prev, 
            check_in: e.target.value,
            expected_checkout: checkOutDate.toISOString().slice(0, 16) 
          }));
        }
      }}
      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
    />
  </div>

  <div className="space-y-2">
    <label className="block text-sm font-medium text-gray-700">
      Salida Esperada
    </label>
    <input
      type="datetime-local"
      value={formData.expected_checkout || ''}
      readOnly={formData.stay_type === 'hourly'}
      onChange={(e) => setFormData({ ...formData, expected_checkout: e.target.value })}
      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 bg-gray-50"
    />
  </div>
</div>

{/* NUEVA SECCIÓN: Descuentos */}
<div className="space-y-2">
  <label className="block text-sm font-medium text-gray-700">
    Descuento (Bs.)
  </label>
  <input
    type="number"
    min="0"
    step="0.01"
    value={formData.discount || 0}
    onChange={(e) => setFormData({ ...formData, discount: parseFloat(e.target.value) || 0 })}
    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
    placeholder="0.00"
  />
</div>

{/* Mostrar precio calculado */}
{selectedRoom && (
  <div className="bg-blue-50 p-4 rounded-lg">
    <div className="flex justify-between mb-2">
      <span className="text-gray-700">Precio base:</span>
      <span className="font-semibold">Bs. {calculatePrice().basePrice.toFixed(2)}</span>
    </div>
    {formData.discount > 0 && (
      <div className="flex justify-between mb-2 text-green-600">
        <span>Descuento:</span>
        <span>- Bs. {parseFloat(formData.discount).toFixed(2)}</span>
      </div>
    )}
    <div className="flex justify-between border-t pt-2">
      <span className="font-bold text-lg">Total a pagar:</span>
      <span className="font-bold text-xl text-blue-600">
        Bs. {calculatePrice().total.toFixed(2)}
      </span>
    </div>
  </div>
)}
            <div className="grid grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-3 max-h-64 overflow-y-auto">
              {rooms.map((room) => (
                <button
                  key={room.id}
                  type="button"
                  onClick={() => handleRoomSelect(room)}
                  className={`border-2 rounded-lg p-3 cursor-pointer transition-all ${
                    formData.room_id === room.id
                      ? 'border-purple-500 bg-purple-100'
                      : 'border-gray-200 hover:border-purple-300'
                  }`}
                >
                  <p className="font-bold text-lg">{room.room_number}</p>
                  <p className="text-xs text-gray-600 capitalize">{room.room_type}</p>
                  <p className="text-sm font-semibold text-green-600 mt-1">
                    Bs. {parseFloat(room.base_price).toFixed(2)}
                  </p>
                </button>
              ))}
            </div>

            {rooms.length === 0 && (
              <p className="text-center text-gray-500 py-8">
                No hay habitaciones disponibles
              </p>
            )}
          </div>

          {/* RESUMEN Y BOTONES */}
          {selectedRoom && (
            <div className="card bg-yellow-50">
              <h3 className="font-bold text-lg mb-4">Resumen del Cobro</h3>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Precio de Habitación:</span>
                  <span className="font-semibold">Bs. {parseFloat(selectedRoom.base_price).toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span>Ingresos Adicionales:</span>
                  <span className="font-semibold">Bs. {parseFloat(formData.additional_income || 0).toFixed(2)}</span>
                </div>
                <div className="flex justify-between border-t pt-2">
                  <span className="font-bold text-lg">TOTAL:</span>
                  <span className="font-bold text-xl text-green-600">
                    Bs. {(parseFloat(selectedRoom.base_price) + parseFloat(formData.additional_income || 0)).toFixed(2)}
                  </span>
                </div>
              </div>
            </div>
          )}

          <div className="flex gap-3">
            <button
              type="submit"
              disabled={loading || !formData.room_id}
              className="btn-success flex-1"
            >
              {loading ? 'Registrando...' : 'Registrar Huésped'}
            </button>
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary flex-1"
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default GuestRegistrationForm;
