import React, { useState, useEffect } from 'react';
import { Users, Plus, DollarSign, LogOut as CheckOutIcon } from 'lucide-react';
import { getActiveBookings } from '../../services/api'; // ‚úÖ CAMBIO AQU√ç
import GuestRegistrationForm from './GuestRegistrationForm';
import CheckoutModal from './CheckoutModal';
import AddChargesModal from './AddChargesModal';

const ActiveGuestsList = () => {
  const [guests, setGuests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showRegistration, setShowRegistration] = useState(false);
  const [selectedGuest, setSelectedGuest] = useState(null);
  const [showCheckout, setShowCheckout] = useState(false);
  const [showAddCharges, setShowAddCharges] = useState(false);

  useEffect(() => {
    loadGuests();
    // Recargar cada 30 segundos para actualizar late checkout
    const interval = setInterval(loadGuests, 30000);
    return () => clearInterval(interval);
  }, []);

  const loadGuests = async () => {
    setLoading(true);
    try {
      // ‚úÖ USAR getActiveBookings EN LUGAR DE searchBookings
      const data = await getActiveBookings();
      console.log('üì¶ Datos recibidos del backend:', data); // Debug
      setGuests(data);
    } catch (error) {
      console.error('‚ùå Error al cargar hu√©spedes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCheckout = (guest) => {
    console.log('üîç Guest seleccionado para checkout:', guest); // Debug
    setSelectedGuest(guest);
    setShowCheckout(true);
  };

  const handleAddCharges = (guest) => {
    setSelectedGuest(guest);
    setShowAddCharges(true);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold flex items-center gap-2">
          <Users className="w-6 h-6" />
          Hu√©spedes Activos ({guests.length})
        </h2>
        <button
          onClick={() => setShowRegistration(true)}
          className="btn-success flex items-center gap-2"
        >
          <Plus className="w-5 h-5" />
          Registrar Hu√©sped
        </button>
      </div>

      {guests.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {guests.map((guest) => {
            // ‚úÖ Extraer valores con nombres correctos del backend
            const total = parseFloat(guest.total_amount || 0);
            const lateCharge = parseFloat(guest.late_checkout_charge || 0);
            const paid = parseFloat(guest.amount_paid || 0);
            const balance = parseFloat(guest.current_balance || guest.balance || 0);
            const isLate = guest.is_late === true || guest.is_late === 1;
            const hoursLate = parseInt(guest.late_checkout_hours || 0);

            console.log(`üîç Guest ${guest.customer_name}:`, {
              total,
              lateCharge,
              paid,
              balance,
              isLate,
              hoursLate
            });

            return (
              <div key={guest.id} className="card hover:shadow-lg transition-shadow">
                {/* ‚úÖ Alerta de Late Checkout */}
                {isLate && hoursLate > 0 && (
                  <div className="bg-red-50 border-l-4 border-red-500 p-2 mb-3 rounded">
                    <p className="text-xs font-bold text-red-800">
                      üö® SALIDA TARD√çA: {hoursLate}h
                    </p>
                  </div>
                )}

                {/* Cabecera */}
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className="font-bold text-lg">{guest.customer_name || guest.full_name}</h3>
                    <p className="text-sm text-gray-600">CI: {guest.document_number}</p>
                  </div>
                  <span className={`badge ${isLate ? 'badge-danger' : 'badge-success'}`}>
                    Hab. {guest.room_number}
                  </span>
                </div>

                {/* Informaci√≥n */}
                <div className="space-y-2 text-sm mb-4">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Check-in:</span>
                    <span className="font-semibold">
                      {new Date(guest.check_in).toLocaleString('es-BO', {
                        day: '2-digit',
                        month: '2-digit',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Salida esperada:</span>
                    <span className={`font-semibold ${isLate ? 'text-red-600' : ''}`}>
                      {new Date(guest.expected_checkout).toLocaleString('es-BO', {
                        day: '2-digit',
                        month: '2-digit',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                      {isLate && <span className="ml-1 text-xs">(VENCIDO)</span>}
                    </span>
                  </div>
                </div>

                {/* ‚úÖ Montos con Late Checkout Visible */}
                <div className="border-t pt-3 space-y-2 mb-4">
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Total habitaci√≥n:</span>
                    <span className="font-semibold">Bs. {total.toFixed(2)}</span>
                  </div>
                  
                  {/* ‚úÖ MOSTRAR LATE CHECKOUT SI EXISTE */}
                  {lateCharge > 0 && (
                    <div className="flex justify-between text-sm bg-orange-50 p-2 rounded border border-orange-200">
                      <span className="text-orange-700 font-medium">
                        ‚è∞ Late Checkout ({hoursLate}h):
                      </span>
                      <span className="text-orange-700 font-bold">
                        + Bs. {lateCharge.toFixed(2)}
                      </span>
                    </div>
                  )}
                  
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Pagado:</span>
                    <span className="text-green-600 font-semibold">
                      Bs. {paid.toFixed(2)}
                    </span>
                  </div>
                  
                  {/* ‚úÖ SALDO = Total + Late Checkout - Pagado */}
                  <div className="flex justify-between text-sm border-t-2 pt-2 mt-2">
                    <span className="text-gray-800 font-bold text-base">SALDO:</span>
                    <span className={`font-bold text-xl ${
                      balance > 0.01 ? 'text-red-600' : 'text-green-600'
                    }`}>
                      Bs. {balance.toFixed(2)}
                    </span>
                  </div>

                  {/* ‚úÖ Explicaci√≥n del c√°lculo si hay late checkout */}
                  {lateCharge > 0 && (
                    <div className="bg-blue-50 p-2 rounded text-xs text-blue-800 border border-blue-200">
                      <p className="font-medium">
                        üí° C√°lculo: {total.toFixed(2)} + {lateCharge.toFixed(2)} - {paid.toFixed(2)} = <strong>{balance.toFixed(2)}</strong>
                      </p>
                    </div>
                  )}
                </div>

                {/* Botones */}
                <div className="grid grid-cols-2 gap-2">
                  <button
                    onClick={() => handleAddCharges(guest)}
                    className="btn-secondary text-sm flex items-center justify-center gap-1"
                  >
                    <DollarSign className="w-4 h-4" />
                    Cargos
                  </button>
                  <button
                    onClick={() => handleCheckout(guest)}
                    className={`text-sm flex items-center justify-center gap-1 font-semibold ${
                      balance > 0.01 
                        ? 'bg-orange-500 hover:bg-orange-600 text-white rounded-lg' 
                        : 'btn-primary'
                    }`}
                  >
                    <CheckOutIcon className="w-4 h-4" />
                    {balance > 0.01 ? 'üí∞ Cobrar' : 'Salida'}
                  </button>
                </div>

                {/* ‚úÖ Advertencia si hay deuda */}
                {balance > 0.01 && (
                  <p className="text-xs text-center text-red-600 mt-2 font-bold">
                    ‚ö†Ô∏è Debe cobrar Bs. {balance.toFixed(2)}
                  </p>
                )}
              </div>
            );
          })}
        </div>
      ) : (
        <div className="card text-center py-12">
          <Users className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 text-lg mb-4">No hay hu√©spedes registrados</p>
          <button
            onClick={() => setShowRegistration(true)}
            className="btn-success mx-auto"
          >
            Registrar Primer Hu√©sped
          </button>
        </div>
      )}

      {/* Modales */}
      {showRegistration && (
        <GuestRegistrationForm
          onClose={() => setShowRegistration(false)}
          onCreated={() => {
            setShowRegistration(false);
            loadGuests();
          }}
        />
      )}

      {showCheckout && selectedGuest && (
        <CheckoutModal
          booking={selectedGuest}
          onClose={() => {
            setShowCheckout(false);
            setSelectedGuest(null);
          }}
          onSuccess={() => {
            setShowCheckout(false);
            setSelectedGuest(null);
            loadGuests();
          }}
        />
      )}

      {showAddCharges && selectedGuest && (
        <AddChargesModal
          guest={selectedGuest}
          onClose={() => {
            setShowAddCharges(false);
            setSelectedGuest(null);
          }}
          onAdded={() => {
            setShowAddCharges(false);
            setSelectedGuest(null);
            loadGuests();
          }}
        />
      )}
    </div>
  );
};

export default ActiveGuestsList;
