import React, { useState, useEffect } from 'react';
import { X, UserPlus } from 'lucide-react';
import { getAvailableRooms, createGuestBooking } from '../../services/api';

const GuestRegistrationForm = ({ onClose, onCreated }) => {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  
  // Obtener hora actual en formato correcto
  const getCurrentDateTime = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const hours = String(now.getHours()).padStart(2, '0');
    const minutes = String(now.getMinutes()).padStart(2, '0');
    return `${year}-${month}-${day}T${hours}:${minutes}`;
  };

  const [formData, setFormData] = useState({
    // Datos del huÃ©sped
    full_name: '',
    document_number: '',
    age: '',
    nationality: 'Bolivia',
    origin: '',
    phone: '',
    
    // Datos de la reserva
    room_id: '',
    stay_type: 'night', // 'hours' o 'night'
    number_of_hours: 3, // MÃ­nimo 3 horas
    check_in: getCurrentDateTime(), // HORA ACTUAL
    expected_checkout: '',
    base_price: 0,
    price_3h: 0, // Precio por 3 horas desde la BD
    additional_income: '0',
  });

  useEffect(() => {
    loadRooms();
  }, []);

  const loadRooms = async () => {
    try {
      const data = await getAvailableRooms();
      setRooms(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleRoomSelect = (room) => {
    setFormData({
      ...formData,
      room_id: room.id,
      base_price: parseFloat(room.base_price),
      price_3h: parseFloat(room.short_stay_3h_price) || 0
    });
  };

  // Calcular checkout automÃ¡ticamente
  useEffect(() => {
    if (!formData.check_in) return;

    const checkInDate = new Date(formData.check_in);
    let checkOutDate;

    if (formData.stay_type === 'hours') {
      // Por horas: sumar las horas al check-in
      checkOutDate = new Date(checkInDate.getTime() + (formData.number_of_hours * 60 * 60 * 1000));
    } else {
      // Por noche: siguiente dÃ­a a las 12:00 PM
      checkOutDate = new Date(checkInDate);
      checkOutDate.setDate(checkOutDate.getDate() + 1);
      checkOutDate.setHours(12, 0, 0, 0);
    }

    const year = checkOutDate.getFullYear();
    const month = String(checkOutDate.getMonth() + 1).padStart(2, '0');
    const day = String(checkOutDate.getDate()).padStart(2, '0');
    const hours = String(checkOutDate.getHours()).padStart(2, '0');
    const minutes = String(checkOutDate.getMinutes()).padStart(2, '0');
    const checkoutStr = `${year}-${month}-${day}T${hours}:${minutes}`;

    setFormData(prev => ({ ...prev, expected_checkout: checkoutStr }));
  }, [formData.stay_type, formData.number_of_hours, formData.check_in]);

  // Calcular precio total
  const calculateTotalPrice = () => {
    if (!formData.room_id || !formData.base_price) return 0;

    let calculatedPrice = 0;

    if (formData.stay_type === 'hours') {
      // PRECIO POR HORAS: Usa el precio de 3 horas configurado por el admin
      if (!formData.price_3h || formData.price_3h === 0) {
        alert('âš ï¸ Esta habitaciÃ³n no tiene precio por horas configurado');
        return 0;
      }
      // Precio por hora = price_3h / 3
      const pricePerHour = formData.price_3h / 3;
      calculatedPrice = pricePerHour * formData.number_of_hours;
    } else {
      // Por noche: precio base
      calculatedPrice = formData.base_price;
    }

    const additional = parseFloat(formData.additional_income) || 0;
    return calculatedPrice + additional;
  };

  // Calcular precio por hora
  const getPricePerHour = () => {
    if (!formData.price_3h || formData.price_3h === 0) return 0;
    return formData.price_3h / 3;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validar precio por horas
    if (formData.stay_type === 'hours' && (!formData.price_3h || formData.price_3h === 0)) {
      alert('âš ï¸ Esta habitaciÃ³n no tiene precio por horas configurado. Por favor selecciona otra habitaciÃ³n o cambia a "Por Noche".');
      return;
    }

    setLoading(true);

    try {
      const totalPrice = calculateTotalPrice();
      
      const bookingData = {
        full_name: formData.full_name,
        document_number: formData.document_number,
        phone: formData.phone,
        age: parseInt(formData.age),
        nationality: formData.nationality,
        origin: formData.origin,
        room_id: formData.room_id,
        check_in: formData.check_in,
        expected_checkout: formData.expected_checkout,
        stay_type: formData.stay_type === 'hours' ? 'hourly' : 'daily',
        number_of_hours: formData.stay_type === 'hours' ? formData.number_of_hours : null,
        base_price: totalPrice.toFixed(2),
        additional_income: parseFloat(formData.additional_income) || 0
      };

      await createGuestBooking(bookingData);
      alert('âœ… HuÃ©sped registrado exitosamente');
      onCreated();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al registrar huÃ©sped');
    } finally {
      setLoading(false);
    }
  };

  const selectedRoom = rooms.find(r => r.id === formData.room_id);
  const totalPrice = calculateTotalPrice();
  const pricePerHour = getPricePerHour();

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-y-auto">
      <div className="bg-white rounded-lg p-6 max-w-4xl w-full mx-4 my-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold flex items-center gap-2">
            <UserPlus className="w-6 h-6" />
            Registrar Nuevo HuÃ©sped
          </h2>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* PASO 1: DATOS DEL HUÃ‰SPED */}
          <div className="card bg-blue-50">
            <h3 className="font-bold text-lg mb-4">ðŸ“‹ Paso 1: Datos del HuÃ©sped</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="label">Nombre Completo *</label>
                <input
                  type="text"
                  className="input"
                  value={formData.full_name}
                  onChange={(e) => setFormData({...formData, full_name: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">NÃºmero de Documento *</label>
                <input
                  type="text"
                  className="input"
                  value={formData.document_number}
                  onChange={(e) => setFormData({...formData, document_number: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Edad *</label>
                <input
                  type="number"
                  className="input"
                  min="1"
                  max="120"
                  value={formData.age}
                  onChange={(e) => setFormData({...formData, age: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Nacionalidad *</label>
                <input
                  type="text"
                  className="input"
                  value={formData.nationality}
                  onChange={(e) => setFormData({...formData, nationality: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">Procedencia (Ciudad/PaÃ­s) *</label>
                <input
                  type="text"
                  className="input"
                  placeholder="Ej: La Paz, Bolivia"
                  value={formData.origin}
                  onChange={(e) => setFormData({...formData, origin: e.target.value})}
                  required
                />
              </div>

              <div>
                <label className="label">NÃºmero de TelÃ©fono *</label>
                <input
                  type="tel"
                  className="input"
                  placeholder="+59171234567"
                  value={formData.phone}
                  onChange={(e) => setFormData({...formData, phone: e.target.value})}
                  required
                />
              </div>
            </div>
          </div>

          {/* PASO 2: SELECCIÃ“N DE HABITACIÃ“N */}
          <div className="card bg-purple-50">
            <h3 className="font-bold text-lg mb-4">ðŸ¨ Paso 2: Seleccionar HabitaciÃ³n *</h3>
            
            {selectedRoom && (
              <div className="mb-4 p-4 bg-white rounded-lg border-2 border-purple-500">
                <div className="flex justify-between items-center mb-3">
                  <div>
                    <p className="font-bold text-xl">HabitaciÃ³n {selectedRoom.room_number}</p>
                    <p className="text-sm text-gray-600 capitalize">{selectedRoom.room_type}</p>
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-3">
                  <div className="bg-gray-50 p-3 rounded">
                    <p className="text-xs text-gray-600">Precio por noche</p>
                    <p className="text-lg font-bold text-blue-600">
                      Bs. {parseFloat(selectedRoom.base_price).toFixed(2)}
                    </p>
                  </div>
                  <div className="bg-gray-50 p-3 rounded">
                    <p className="text-xs text-gray-600">Precio por hora</p>
                    <p className="text-lg font-bold text-green-600">
                      {selectedRoom.short_stay_3h_price 
                        ? `Bs. ${(parseFloat(selectedRoom.short_stay_3h_price) / 3).toFixed(2)}`
                        : 'No disponible'}
                    </p>
                    {selectedRoom.short_stay_3h_price && (
                      <p className="text-xs text-gray-500 mt-1">
                        (3h = Bs. {parseFloat(selectedRoom.short_stay_3h_price).toFixed(2)})
                      </p>
                    )}
                  </div>
                </div>
              </div>
            )}

            <div className="grid grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-3 max-h-64 overflow-y-auto">
              {rooms.map((room) => (
                <button
                  key={room.id}
                  type="button"
                  onClick={() => handleRoomSelect(room)}
                  className={`border-2 rounded-lg p-3 cursor-pointer transition-all ${
                    formData.room_id === room.id
                      ? 'border-purple-500 bg-purple-100 shadow-lg'
                      : 'border-gray-200 hover:border-purple-300'
                  }`}
                >
                  <p className="font-bold text-lg">{room.room_number}</p>
                  <p className="text-xs text-gray-600 capitalize">{room.room_type}</p>
                  <p className="text-sm font-semibold text-green-600 mt-1">
                    Bs. {parseFloat(room.base_price).toFixed(2)}
                  </p>
                  {room.short_stay_3h_price && (
                    <p className="text-xs text-blue-600">
                      â±ï¸ Disponible por horas
                    </p>
                  )}
                </button>
              ))}
            </div>

            {rooms.length === 0 && (
              <p className="text-center text-gray-500 py-8">
                No hay habitaciones disponibles
              </p>
            )}
          </div>

          {/* PASO 3: TIPO DE ESTADÃA Y HORARIOS */}
          {selectedRoom && (
            <div className="card bg-green-50">
              <h3 className="font-bold text-lg mb-4">â° Paso 3: Tipo de EstadÃ­a y Horarios</h3>
              
              {/* Tipo de EstadÃ­a */}
              <div className="mb-4">
                <label className="label">Tipo de EstadÃ­a *</label>
                <div className="grid grid-cols-2 gap-4">
                  <button
                    type="button"
                    onClick={() => setFormData({ ...formData, stay_type: 'night' })}
                    className={`p-4 border-2 rounded-lg transition-all ${
                      formData.stay_type === 'night'
                        ? 'border-green-500 bg-green-100 shadow-lg'
                        : 'border-gray-300 hover:border-green-300'
                    }`}
                  >
                    <div className="text-3xl mb-2">ðŸŒ™</div>
                    <div className="font-bold">Por Noche</div>
                    <div className="text-sm text-gray-600">1 noche completa</div>
                    <div className="text-lg font-bold text-green-600 mt-2">
                      Bs. {parseFloat(selectedRoom.base_price).toFixed(2)}
                    </div>
                  </button>

                  <button
                    type="button"
                    onClick={() => setFormData({ ...formData, stay_type: 'hours', number_of_hours: 3 })}
                    disabled={!selectedRoom.short_stay_3h_price}
                    className={`p-4 border-2 rounded-lg transition-all ${
                      !selectedRoom.short_stay_3h_price
                        ? 'border-gray-200 bg-gray-100 cursor-not-allowed opacity-50'
                        : formData.stay_type === 'hours'
                        ? 'border-green-500 bg-green-100 shadow-lg'
                        : 'border-gray-300 hover:border-green-300'
                    }`}
                  >
                    <div className="text-3xl mb-2">â±ï¸</div>
                    <div className="font-bold">Por Horas</div>
                    <div className="text-sm text-gray-600">
                      {selectedRoom.short_stay_3h_price ? 'MÃ­nimo 3 horas' : 'No disponible'}
                    </div>
                    {selectedRoom.short_stay_3h_price && (
                      <div className="text-lg font-bold text-green-600 mt-2">
                        Bs. {(parseFloat(selectedRoom.short_stay_3h_price) / 3).toFixed(2)}/hora
                      </div>
                    )}
                  </button>
                </div>
              </div>

              {/* NÃºmero de Horas (solo si es por horas) */}
              {formData.stay_type === 'hours' && (
                <div className="mb-4">
                  <label className="label">Cantidad de Horas (MÃ­nimo 3) *</label>
                  <input
                    type="number"
                    className="input"
                    min="3"
                    max="24"
                    value={formData.number_of_hours}
                    onChange={(e) => setFormData({ ...formData, number_of_hours: parseInt(e.target.value) || 3 })}
                    required
                  />
                  <p className="text-sm text-gray-600 mt-1">
                    ðŸ“Š Precio por hora: Bs. {pricePerHour.toFixed(2)}
                    <br />
                    ðŸ’¡ Base: Bs. {formData.price_3h.toFixed(2)} por 3 horas
                  </p>
                </div>
              )}

              {/* Check-in y Check-out */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="label">Check-in (Hora Actual) *</label>
                  <input
                    type="datetime-local"
                    className="input bg-yellow-50"
                    value={formData.check_in}
                    onChange={(e) => setFormData({ ...formData, check_in: e.target.value })}
                    required
                  />
                  <p className="text-xs text-gray-600 mt-1">
                    â° Predeterminado: Hora actual
                  </p>
                </div>

                <div>
                  <label className="label">Check-out (AutomÃ¡tico) *</label>
                  <input
                    type="datetime-local"
                    className="input bg-gray-100"
                    value={formData.expected_checkout}
                    readOnly
                    required
                  />
                  <p className="text-xs text-gray-600 mt-1">
                    {formData.stay_type === 'hours' 
                      ? `ðŸ“ ${formData.number_of_hours} horas despuÃ©s del check-in`
                      : 'ðŸ“ Siguiente dÃ­a a las 12:00 PM'}
                  </p>
                </div>
              </div>

              {/* Ingresos Adicionales */}
              <div className="mt-4">
                <label className="label">Ingresos Adicionales (Opcional)</label>
                <input
                  type="number"
                  step="0.01"
                  className="input"
                  value={formData.additional_income}
                  onChange={(e) => setFormData({...formData, additional_income: e.target.value})}
                  placeholder="0.00"
                />
              </div>
            </div>
          )}

          {/* RESUMEN FINAL */}
          {selectedRoom && (
            <div className="card bg-yellow-50 border-2 border-yellow-400">
              <h3 className="font-bold text-xl mb-4">ðŸ’° Resumen del Cobro</h3>
              <div className="space-y-3">
                <div className="flex justify-between text-lg">
                  <span className="text-gray-700">
                    {formData.stay_type === 'hours' 
                      ? `HabitaciÃ³n por ${formData.number_of_hours} horas:` 
                      : 'HabitaciÃ³n por 1 noche:'}
                  </span>
                  <span className="font-semibold">
                    Bs. {formData.stay_type === 'hours'
                      ? (pricePerHour * formData.number_of_hours).toFixed(2)
                      : parseFloat(selectedRoom.base_price).toFixed(2)}
                  </span>
                </div>

                {formData.stay_type === 'hours' && (
                  <div className="bg-blue-50 p-2 rounded text-sm">
                    <p className="text-gray-700">
                      ðŸ“Š CÃ¡lculo: Bs. {formData.price_3h.toFixed(2)} (3h) Ã· 3 = Bs. {pricePerHour.toFixed(2)}/hora
                      <br />
                      ðŸ’µ Total: Bs. {pricePerHour.toFixed(2)} Ã— {formData.number_of_hours}h = Bs. {(pricePerHour * formData.number_of_hours).toFixed(2)}
                    </p>
                  </div>
                )}

                {parseFloat(formData.additional_income || 0) > 0 && (
                  <div className="flex justify-between text-lg text-blue-600">
                    <span>Ingresos adicionales:</span>
                    <span className="font-semibold">+ Bs. {parseFloat(formData.additional_income).toFixed(2)}</span>
                  </div>
                )}

                <div className="border-t-2 border-yellow-600 pt-3 flex justify-between">
                  <span className="font-bold text-2xl">TOTAL A PAGAR:</span>
                  <span className="font-bold text-3xl text-green-600">
                    Bs. {totalPrice.toFixed(2)}
                  </span>
                </div>
              </div>
            </div>
          )}

          {/* BOTONES */}
          <div className="flex gap-3">
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary flex-1"
            >
              Cancelar
            </button>
            <button
              type="submit"
              disabled={loading || !formData.room_id}
              className="btn-success flex-1 text-lg font-bold"
            >
              {loading ? 'Registrando...' : 'âœ… Registrar HuÃ©sped'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

