import React, { useState, useEffect } from 'react';
import { X, CreditCard, DollarSign, Tag, AlertCircle, CheckCircle, Printer } from 'lucide-react';
import { createPayment, checkout, getDiscountPresets, applyDiscount } from '../../services/api';

const CheckoutModal = ({ booking, onClose, onSuccess }) => {
  const [step, setStep] = useState(booking.current_balance > 0.01 ? 'payment' : 'confirm');
  const [discountPresets, setDiscountPresets] = useState([]);
  const [loading, setLoading] = useState(false);

  // Estado del pago
  const [paymentData, setPaymentData] = useState({
    amount: booking.current_balance.toFixed(2),
    payment_method: 'cash',
    card_last_digits: '',
    transaction_reference: '',
    notes: ''
  });

  // Estado del descuento
  const [selectedDiscount, setSelectedDiscount] = useState(null);
  const [customDiscount, setCustomDiscount] = useState({
    type: 'percentage',
    value: '',
    reason: ''
  });

  useEffect(() => {
    loadDiscounts();
  }, []);

  const loadDiscounts = async () => {
    try {
      const data = await getDiscountPresets();
      setDiscountPresets(data.filter(d => d.is_active));
    } catch (error) {
      console.error('Error al cargar descuentos:', error);
    }
  };

  const calculateDiscount = () => {
    if (!selectedDiscount && !customDiscount.value) return 0;

    const baseAmount = parseFloat(booking.total_amount) + 
                      (booking.late_checkout_info?.late_checkout_charge || 0);

    if (selectedDiscount) {
      if (selectedDiscount.discount_type === 'percentage') {
        return (baseAmount * parseFloat(selectedDiscount.discount_value)) / 100;
      }
      return parseFloat(selectedDiscount.discount_value);
    }

    if (customDiscount.value) {
      if (customDiscount.type === 'percentage') {
        return (baseAmount * parseFloat(customDiscount.value)) / 100;
      }
      return parseFloat(customDiscount.value);
    }

    return 0;
  };

  const discountAmount = calculateDiscount();
  const totalWithDiscount = booking.current_balance - discountAmount;
  const finalBalance = totalWithDiscount - parseFloat(paymentData.amount || 0);

  const handleApplyDiscount = () => {
    // AquÃ­ aplicarÃ­as el descuento al booking
    setStep('payment');
    setPaymentData({ ...paymentData, amount: totalWithDiscount.toFixed(2) });
  };

  const handlePayment = async () => {
    if (parseFloat(paymentData.amount) <= 0) {
      alert('El monto debe ser mayor a 0');
      return;
    }

    if (paymentData.payment_method === 'card' && !paymentData.card_last_digits) {
      alert('Ingrese los Ãºltimos 4 dÃ­gitos de la tarjeta');
      return;
    }

    setLoading(true);
    try {
      await createPayment(paymentData);
      
      if (finalBalance <= 0.01) {
        setStep('confirm');
      } else {
        alert(`Pago registrado. Saldo restante: Bs. ${finalBalance.toFixed(2)}`);
        onSuccess();
      }
    } catch (error) {
      alert(error.response?.data?.error || 'Error al registrar pago');
    } finally {
      setLoading(false);
    }
  };

  const handleCheckout = async () => {
    if (finalBalance > 0.01) {
      alert('Debe cobrar el saldo pendiente antes del checkout');
      return;
    }

    setLoading(true);
    try {
      await checkout(booking.id);
      alert('âœ… Check-out realizado exitosamente');
      onSuccess();
    } catch (error) {
      alert(error.response?.data?.error || 'Error al realizar checkout');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-6 sticky top-0">
          <div className="flex justify-between items-start">
            <div>
              <h2 className="text-2xl font-bold mb-1">Proceso de Checkout</h2>
              <p className="text-blue-100">
                {booking.full_name} â€¢ HAB {booking.room_number}
              </p>
              <p className="text-xs text-blue-200 mt-1">{booking.booking_code}</p>
            </div>
            <button onClick={onClose} className="text-white hover:bg-white/20 p-2 rounded">
              <X className="w-6 h-6" />
            </button>
          </div>

          {/* Steps */}
          <div className="flex gap-4 mt-6">
            <div className={`flex-1 ${step === 'discount' ? 'opacity-100' : 'opacity-50'}`}>
              <div className="flex items-center gap-2">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  step === 'discount' ? 'bg-white text-blue-600' : 'bg-blue-500'
                }`}>
                  <Tag className="w-4 h-4" />
                </div>
                <span className="text-sm font-medium">1. Descuentos</span>
              </div>
            </div>
            <div className={`flex-1 ${step === 'payment' ? 'opacity-100' : 'opacity-50'}`}>
              <div className="flex items-center gap-2">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  step === 'payment' ? 'bg-white text-blue-600' : 'bg-blue-500'
                }`}>
                  <CreditCard className="w-4 h-4" />
                </div>
                <span className="text-sm font-medium">2. Pago</span>
              </div>
            </div>
            <div className={`flex-1 ${step === 'confirm' ? 'opacity-100' : 'opacity-50'}`}>
              <div className="flex items-center gap-2">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  step === 'confirm' ? 'bg-white text-blue-600' : 'bg-blue-500'
                }`}>
                  <CheckCircle className="w-4 h-4" />
                </div>
                <span className="text-sm font-medium">3. Confirmar</span>
              </div>
            </div>
          </div>
        </div>

        <div className="p-6">
          {/* Resumen Financiero Siempre Visible */}
          <div className="bg-gray-50 rounded-lg p-4 mb-6 border-2 border-gray-200">
            <h3 className="font-semibold mb-3 text-gray-700">Resumen de Cuenta</h3>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span>HabitaciÃ³n ({booking.number_of_nights} noche{booking.number_of_nights > 1 ? 's' : ''}):</span>
                <span className="font-semibold">Bs. {parseFloat(booking.base_price * booking.number_of_nights).toFixed(2)}</span>
              </div>
              
              {parseFloat(booking.additional_charges) > 0 && (
                <div className="flex justify-between">
                  <span>Cargos adicionales:</span>
                  <span className="font-semibold">Bs. {parseFloat(booking.additional_charges).toFixed(2)}</span>
                </div>
              )}

              {booking.late_checkout_info?.is_late && (
                <div className="flex justify-between text-orange-600">
                  <span>Late checkout ({booking.late_checkout_info.hours_late}h):</span>
                  <span className="font-semibold">
                    Bs. {booking.late_checkout_info.late_checkout_charge.toFixed(2)}
                  </span>
                </div>
              )}

              {discountAmount > 0 && (
                <div className="flex justify-between text-green-600">
                  <span>Descuento aplicado:</span>
                  <span className="font-semibold">-Bs. {discountAmount.toFixed(2)}</span>
                </div>
              )}

              <div className="border-t pt-2 flex justify-between font-bold text-lg">
                <span>TOTAL A PAGAR:</span>
                <span className="text-blue-600">Bs. {totalWithDiscount.toFixed(2)}</span>
              </div>

              <div className="flex justify-between text-green-600">
                <span>Ya pagado:</span>
                <span className="font-semibold">Bs. {parseFloat(booking.amount_paid).toFixed(2)}</span>
              </div>

              <div className="border-t pt-2 flex justify-between font-bold text-xl">
                <span>SALDO:</span>
                <span className={finalBalance > 0.01 ? 'text-red-600' : 'text-green-600'}>
                  Bs. {finalBalance.toFixed(2)}
                </span>
              </div>
            </div>
          </div>

          {/* STEP 1: Aplicar Descuento */}
          {step === 'discount' && (
            <div className="space-y-4">
              <h3 className="font-semibold text-lg">Aplicar Descuento (Opcional)</h3>
              
              {/* Descuentos Predefinidos */}
              <div>
                <label className="label">Descuentos Disponibles:</label>
                <div className="grid grid-cols-2 gap-3">
                  {discountPresets.map(discount => (
                    <button
                      key={discount.id}
                      onClick={() => {
                        setSelectedDiscount(discount);
                        setCustomDiscount({ type: 'percentage', value: '', reason: '' });
                      }}
                      className={`p-4 border-2 rounded-lg text-left transition-all ${
                        selectedDiscount?.id === discount.id
                          ? 'border-green-500 bg-green-50'
                          : 'border-gray-200 hover:border-green-300'
                      }`}
                    >
                      <div className="flex justify-between items-start mb-2">
                        <span className="font-semibold">{discount.name}</span>
                        <span className="badge badge-success">
                          {discount.discount_type === 'percentage' 
                            ? `${discount.discount_value}%` 
                            : `Bs. ${discount.discount_value}`}
                        </span>
                      </div>
                      <p className="text-xs text-gray-600">{discount.reason}</p>
                      {discount.requires_authorization && (
                        <p className="text-xs text-orange-600 mt-1">âš ï¸ Requiere autorizaciÃ³n</p>
                      )}
                    </button>
                  ))}
                </div>
              </div>

              {/* Descuento Personalizado */}
              <div className="border-t pt-4">
                <label className="label">O ingresa un descuento personalizado:</label>
                <div className="grid grid-cols-3 gap-3">
                  <select
                    className="input"
                    value={customDiscount.type}
                    onChange={(e) => {
                      setCustomDiscount({ ...customDiscount, type: e.target.value });
                      setSelectedDiscount(null);
                    }}
                  >
                    <option value="percentage">Porcentaje (%)</option>
                    <option value="fixed">Monto Fijo (Bs.)</option>
                  </select>
                  <input
                    type="number"
                    className="input"
                    placeholder="Valor"
                    step="0.01"
                    value={customDiscount.value}
                    onChange={(e) => {
                      setCustomDiscount({ ...customDiscount, value: e.target.value });
                      setSelectedDiscount(null);
                    }}
                  />
                  <input
                    type="text"
                    className="input"
                    placeholder="RazÃ³n"
                    value={customDiscount.reason}
                    onChange={(e) => setCustomDiscount({ ...customDiscount, reason: e.target.value })}
                  />
                </div>
              </div>

              {discountAmount > 0 && (
                <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                  <p className="text-green-800 font-semibold">
                    âœ“ Descuento: Bs. {discountAmount.toFixed(2)}
                  </p>
                  <p className="text-sm text-green-700">
                    Nuevo total: Bs. {totalWithDiscount.toFixed(2)}
                  </p>
                </div>
              )}

              <div className="flex gap-3">
                <button
                  onClick={() => setStep('payment')}
                  className="btn-secondary flex-1"
                >
                  Saltar (sin descuento)
                </button>
                <button
                  onClick={handleApplyDiscount}
                  disabled={discountAmount === 0}
                  className="btn-primary flex-1"
                >
                  Aplicar y Continuar
                </button>
              </div>
            </div>
          )}

          {/* STEP 2: Registrar Pago */}
          {step === 'payment' && (
            <div className="space-y-4">
              <h3 className="font-semibold text-lg">Registrar Pago</h3>

              <div>
                <label className="label">Monto a Cobrar *</label>
                <input
                  type="number"
                  className="input text-2xl font-bold"
                  step="0.01"
                  value={paymentData.amount}
                  onChange={(e) => setPaymentData({ ...paymentData, amount: e.target.value })}
                />
                <div className="flex gap-2 mt-2">
                  <button
                    onClick={() => setPaymentData({ ...paymentData, amount: totalWithDiscount.toFixed(2) })}
                    className="text-sm btn-secondary"
                  >
                    Total completo
                  </button>
                  <button
                    onClick={() => setPaymentData({ ...paymentData, amount: (totalWithDiscount / 2).toFixed(2) })}
                    className="text-sm btn-secondary"
                  >
                    50%
                  </button>
                </div>
              </div>

              <div>
                <label className="label">MÃ©todo de Pago *</label>
                <div className="grid grid-cols-3 gap-3">
                  {[
                    { value: 'cash', label: 'Efectivo', icon: 'ðŸ’µ' },
                    { value: 'card', label: 'Tarjeta/QR', icon: 'ðŸ’³' },
                    { value: 'transfer', label: 'Transferencia', icon: 'ðŸ¦' }
                  ].map(method => (
                    <button
                      key={method.value}
                      onClick={() => setPaymentData({ ...paymentData, payment_method: method.value })}
                      className={`p-4 border-2 rounded-lg transition-all ${
                        paymentData.payment_method === method.value
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-blue-300'
                      }`}
                    >
                      <div className="text-2xl mb-1">{method.icon}</div>
                      <div className="text-sm font-medium">{method.label}</div>
                    </button>
                  ))}
                </div>
              </div>

              {paymentData.payment_method === 'card' && (
                <div>
                  <label className="label">Ãšltimos 4 dÃ­gitos</label>
                  <input
                    type="text"
                    className="input"
                    maxLength="4"
                    placeholder="1234"
                    value={paymentData.card_last_digits}
                    onChange={(e) => setPaymentData({ ...paymentData, card_last_digits: e.target.value })}
                  />
                </div>
              )}

              {(paymentData.payment_method === 'transfer' || paymentData.payment_method === 'card') && (
                <div>
                  <label className="label">Referencia/Nro. TransacciÃ³n</label>
                  <input
                    type="text"
                    className="input"
                    placeholder="Ej: TRX123456"
                    value={paymentData.transaction_reference}
                    onChange={(e) => setPaymentData({ ...paymentData, transaction_reference: e.target.value })}
                  />
                </div>
              )}

              <div>
                <label className="label">Notas (Opcional)</label>
                <textarea
                  className="input"
                  rows="2"
                  placeholder="Observaciones del pago..."
                  value={paymentData.notes}
                  onChange={(e) => setPaymentData({ ...paymentData, notes: e.target.value })}
                />
              </div>

              {finalBalance < -0.01 && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                  <AlertCircle className="w-5 h-5 text-yellow-600 inline mr-2" />
                  <span className="text-yellow-800 font-semibold">
                    Cambio a devolver: Bs. {Math.abs(finalBalance).toFixed(2)}
                  </span>
                </div>
              )}

              <div className="flex gap-3">
                <button
                  onClick={() => setStep('discount')}
                  className="btn-secondary flex-1"
                >
                  AtrÃ¡s
                </button>
                <button
                  onClick={handlePayment}
                  disabled={loading}
                  className="btn-primary flex-1"
                >
                  {loading ? 'Procesando...' : 'Registrar Pago'}
                </button>
              </div>
            </div>
          )}

          {/* STEP 3: Confirmar Checkout */}
          {step === 'confirm' && (
            <div className="space-y-6">
              <div className="text-center">
                <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
                <h3 className="text-2xl font-bold text-gray-800 mb-2">Â¡Listo para Checkout!</h3>
                <p className="text-gray-600">
                  Todos los pagos han sido registrados correctamente
                </p>
              </div>

              {booking.late_checkout_info?.is_late && (
                <div className="bg-orange-50 border border-orange-200 rounded-lg p-4">
                  <p className="text-orange-800 font-semibold mb-1">
                    â° Salida tardÃ­a: {booking.late_checkout_info.hours_late} hora(s)
                  </p>
                  <p className="text-sm text-orange-700">
                    Cargo adicional cobrado: Bs. {booking.late_checkout_info.late_checkout_charge.toFixed(2)}
                  </p>
                </div>
              )}

              <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                <h4 className="font-semibold text-green-800 mb-2">Resumen Final:</h4>
                <div className="space-y-1 text-sm text-green-700">
                  <div className="flex justify-between">
                    <span>Total facturado:</span>
                    <span className="font-semibold">Bs. {totalWithDiscount.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Total cobrado:</span>
                    <span className="font-semibold">Bs. {totalWithDiscount.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between font-bold">
                    <span>Balance:</span>
                    <span>Bs. 0.00 âœ“</span>
                  </div>
                </div>
              </div>

              <div className="flex gap-3">
                <button
                  onClick={handleCheckout}
                  disabled={loading}
                  className="btn-success flex-1 py-4 text-lg"
                >
                  {loading ? 'Procesando...' : 'âœ“ Confirmar Checkout'}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CheckoutModal;
